const bcu = require('bigint-crypto-utils');

class privateKey{
    d;
    n;
    // publicKey: publicKey

    constructor(d, n){
        this.d = d;
        this.n = n;

    }
}
function decrypt(c) {
    m = bcu.modPow(c, this.d, this.n);
    return m;
}

function sign(m) {
    s = bcu.modPow(m, this.d, this.n);
    return s;
}


// decrypt(c: bigint) -> m:bigint c^d mod n

// sign (m: bigint)-> s: bigint m^d mod n