const bcu = require('bigint-crypto-utils');

// exponente y modulo (bigint # grande)

class publicKey{
    e;
    n;

    constructor(e, n) {
        this.e = e;
        this.n = n;
    }

}
function encrypt(m) {
    c = bcu.modPow(m, this.e, this.n);
    return c;
}

function verify(s) {
    m = bcu.modPow(s, this.e, this.n);
    return m;
}

/* Metodos
-Encriptar mensaje en claro  encrypt(M :bignt) y devuelve c:bigint  c=m^e mod n
-Verificar verify(s: bigint) y devuelve m: bigint
*/
