const rsa = require('./keyGeneration');
const bcu = require('bigint-crypto-utils');
const bco = require('bigint-conversion');

(async function() {
    const { publicKey, privateKey } = await rsa.generateRandomKeys(3072);

    text= "Andrea";

    textBig = bco.textToBigint(text);

    c = publicKey.encrypt(textBig);

    m= bco.bigintToText(privateKey.decrypt(c));

    console.log(c);
    console.log(m);
})();